from typing import List


class Solution:
    def majorityElement(self, nums: List[int]) -> int:
        from math import ceil
        nums = sorted(nums)
        element = nums[ceil(len(nums) / 2) - 1]
        return element


a = Solution()
print(a.majorityElement([3,3,4]))
